
const anchors=document.querySelectorAll('a[href*="#"]')
for(let anchor of anchors){anchor.addEventListener('click',function(e){e.preventDefault()
const blockID=anchor.getAttribute('href')
document.querySelector(''+blockID).scrollIntoView({behavior:'smooth',block:'start'})})}
var menubutton=document.querySelector(".menu-button");var menu=document.querySelector(".site-menu-list");menu.classList.remove("site-menu-list--nojs");menubutton.addEventListener("click",function(evt){evt.preventDefault();menu.classList.toggle("site-menu-list--open");menubutton.classList.toggle("menu-button--menu-open");});var slides=document.querySelectorAll(".slide__item");var label=document.querySelectorAll(".slide__radiolabel");var currentSlide=0;var next=document.querySelector('.slider__btn-next');var previous=document.querySelector('.slider__btn-prev');var slideInterval=setInterval(nextSlide,10000);const color="#d1e44a";var troughLabel=false;function pauseSlideshow(){clearInterval(slideInterval);};label[0].style.backgroundColor=color;next.onclick=function(){troughLabel=false;pauseSlideshow();nextSlide();};previous.onclick=function(){troughLabel=false;pauseSlideshow();previousSlide();};for(var i=0;i<label.length;i++){label[i].onclick=function(){troughLabel=true;pauseSlideshow();goToSlide(this.id);};};function nextSlide(){goToSlide(Number(currentSlide)+1);};function previousSlide(){goToSlide(Number(currentSlide)-1);};function goToSlide(n){label[currentSlide].style.backgroundColor="";slides[currentSlide].classList.remove("slide__item--show");if(troughLabel==true){currentSlide=n;}else{currentSlide=(n+slides.length)%slides.length;}
label[currentSlide].style.backgroundColor=color;slides[currentSlide].classList.add("slide__item--show");};