
const anchors=document.querySelectorAll('a[href*="#"]')
for(let anchor of anchors){anchor.addEventListener('click',function(e){e.preventDefault()
const blockID=anchor.getAttribute('href')
document.querySelector(''+blockID).scrollIntoView({behavior:'smooth',block:'start'})})}
var menubutton=document.querySelector(".menu-button");var menu=document.querySelector(".site-menu-list");menu.classList.remove("site-menu-list--nojs");menubutton.addEventListener("click",function(evt){evt.preventDefault();menu.classList.toggle("site-menu-list--open");menubutton.classList.toggle("menu-button--menu-open");});var slides=document.querySelectorAll(".slide__item");var label=document.querySelectorAll(".slide__radiolabel");var currentSlide=0;var next=document.querySelector('.slider__btn-next');var previous=document.querySelector('.slider__btn-prev');var slideInterval=setInterval(nextSlide,10000);const color="#d1e44a";function pauseSlideshow(){clearInterval(slideInterval);};label[0].style.backgroundColor=color;next.onclick=function(){pauseSlideshow();nextSlide();};previous.onclick=function(){pauseSlideshow();previousSlide();};for(var i=0;i<label.length;i++){label[i].onclick=function(){pauseSlideshow();goToSlide(this.id);};};function nextSlide(){goToSlide(currentSlide+1);};function previousSlide(){goToSlide(currentSlide-1);};function goToSlide(n){label[currentSlide].style.backgroundColor="";slides[currentSlide].classList.remove("slide__item--show");currentSlide=(n+slides.length)%slides.length;label[currentSlide].style.backgroundColor=color;slides[currentSlide].classList.add("slide__item--show");};