
var menubutton=document.querySelector(".menu-button");var menu=document.querySelector(".site-menu-list");menubutton.classList.remove("menu-button--nojs");menu.classList.remove("site-menu-list--nojs");menubutton.addEventListener("click",function(evt){evt.preventDefault();menu.classList.toggle("site-menu-list--open");menubutton.classList.toggle("menu-button--menu-open");});const header=document.querySelector(".header");const pagewrapper=document.querySelector(".page-wrapper");header.classList.remove("header--nojs");pagewrapper.classList.remove("page-wrapper--nojs");const anchor=document.querySelector('.btn--up');anchor.addEventListener('click',function(e){e.preventDefault();const blockID=anchor.getAttribute('href');document.querySelector(''+blockID).scrollIntoView({behavior:'smooth',block:'start'});});var photoOpenBtn=document.querySelectorAll(".goods__show-btn");var photoCloseBtn=document.querySelectorAll(".photo__close");for(var j=0;j<photoOpenBtn.length;j++){photoOpenBtn[j].onclick=function(){document.body.classList.add("body--scroll");var photoBlock=document.getElementById("photo"+this.getAttribute("data-index"));photoBlock.classList.add("photo--show");var photoImg=photoBlock.getElementsByTagName("img");for(var i=0;i<photoImg.length;i++){photoImg[i].setAttribute("src",photoImg[i].getAttribute("data-img"));}
var photoLarge=photoBlock.querySelector(".photo__img-large");var photoSmall=photoBlock.querySelectorAll(".photo__img-small");for(var k=0;k<photoSmall.length;k++){photoSmall[k].onclick=function(){for(var x=0;x<photoSmall.length;x++){photoSmall[x].classList.remove("photo__img-small--active");}
this.classList.add("photo__img-small--active");photoLarge.setAttribute("src",this.getAttribute("src"))};};};};for(var j=0;j<photoCloseBtn.length;j++){photoCloseBtn[j].onclick=function(){document.body.classList.remove("body--scroll");var photoBlock=document.getElementById("photo"+this.getAttribute("data-index"));photoBlock.classList.remove("photo--show");};};window.addEventListener("keydown",function(e){document.body.classList.remove("body--scroll");var photoBlock=document.querySelectorAll(".photo");if(e.keyCode===27){for(var j=0;j<photoBlock.length;j++){if(photoBlock[j].classList.contains("photo--show")){e.preventDefault();photoBlock[j].classList.remove("photo--show");};};};});{var slides=document.querySelectorAll(".slide__item");var label=document.querySelectorAll(".slide__radiolabel");var currentSlide=0;var next=document.querySelector('.slider__btn-next');var previous=document.querySelector('.slider__btn-prev');var slideInterval=setInterval(nextSlide,4000);const color="#d1e44a";var troughLabel=false;const slideselector=document.querySelector(".slide__selector");slideselector.classList.remove("slide__selector--nojs");function pauseSlideshow(){clearInterval(slideInterval);};label[0].style.backgroundColor=color;next.onclick=function(){troughLabel=false;pauseSlideshow();nextSlide();};previous.onclick=function(){troughLabel=false;pauseSlideshow();previousSlide();};for(var i=0;i<label.length;i++){label[i].onclick=function(){troughLabel=true;pauseSlideshow();goToSlide(this.id);};};function nextSlide(){goToSlide(Number(currentSlide)+1);};function previousSlide(){goToSlide(Number(currentSlide)-1);};function goToSlide(n){label[currentSlide].style.backgroundColor="";slides[currentSlide].classList.remove("slide__item--show");if(troughLabel==true){currentSlide=n;}else{currentSlide=(n+slides.length)%slides.length;}
label[currentSlide].style.backgroundColor=color;slides[currentSlide].classList.add("slide__item--show");};}